local QuestID = 1;
local ReqClv = 2;
local ReqJlv = 0;
local NextQuest = 0;
local RewZeny = 100;
local RewCxp = 100;
local RewJxp = 50;
local RewWxp = 0;
local RewItem1 = 1700113;
local RewItem2 = 16098;
local RewItemCount1 = 1;
local RewItemCount2 = 1;
local StepID = 0;

-- Modify steps below for gameplay

function QUEST_START(cid)
	Saga.AddStep(cid, QuestID, 101);
	Saga.AddStep(cid, QuestID, 102);
	Saga.InsertQuest(cid, QuestID, 1);
	return 0;
end

function QUEST_FINISH(cid)
	-- Check for free inventory slot
	local freeslots = Saga.FreeInventoryCount(cid, 0);
	if freeslots > 1 then
		-- Handout all rewards
		Saga.GiveZeny(cid, RewZeny);
		Saga.GiveExp(cid, RewCxp, RewJxp, RewWxp);
		Saga.GiveItem(cid, RewItem1, RewItemCount1);
		Saga.GiveItem(cid, RewItem2, RewItemCount2);
		return 0;
	else
		Saga.EmptyInventory(cid);
		return -1;
	end
end

function QUEST_CANCEL(cid)
	-- Do nothing
	return 0;
end

function QUEST_STEP_1(cid, StepID)
	-- Get 6 Chonchon Fry Wings
	Saga.FindQuestItem(cid, QuestID, StepID, 10026, 2630, 8000, 6, 1);
	Saga.FindQuestItem(cid, QuestID, StepID, 10027, 2630, 8000, 6, 1);
	Saga.FindQuestItem(cid, QuestID, StepID, 10252, 2630, 8000, 6, 1);
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.StepComplete(cid, QuestID, StepID);
	return 0;
end

function QUEST_STEP_2(cid, StepID)
	-- Talk to mischa
	Saga.AddWaypoint(cid, QuestID, StepID, 1, 1000);
	
	-- Check for completion
	local ret = Saga.GetNPCIndex(cid);
	if ret == 1000 then
		Saga.GeneralDialog(cid, 28);
		Saga.NpcTakeItem(cid, 2630, 6);
		Saga.SubstepComplete(cid, QuestID, StepID, 1);
	end
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.ClearWaypoints(cid, QuestID);
	Saga.StepComplete(cid, QuestID, StepID);
	Saga.QuestComplete(cid, QuestID);
	return -1;
end

function QUEST_CHECK(cid)
	-- Check all steps for progress
	local CurStepID = Saga.GetStepIndex(cid, QuestID);
	local ret = -1;
	local StepID = CurStepID;
	
	if CurStepID == 101 then
		ret = QUEST_STEP_1(cid, StepID);
	elseif CurStepID == 102 then
		ret = QUEST_STEP_2(cid, StepID);
	end
	
	if ret == 0 then
		QUEST_CHECK(cid);
	end
	
	return ret;
end
