-- Initialisation des étapes
function QUEST_START(cid)
	Saga.AddStep(cid, QuestID, 14001);
	Saga.AddStep(cid, QuestID, 14002);
	Saga.AddStep(cid, QuestID, 14003);
	Saga.AddStep(cid, QuestID, 14004);
	Saga.InsertQuest(cid, QuestID, 1);
	return 0;
end

function QUEST_FINISH(cid)
	-- Récompenses
	local freeslots = Saga.FreeInventoryCount(cid, 0);
	if freeslots > -1 then
		return 0;
	else
		Saga.EmptyInventory(cid);
		return -1;
	end
end

