local QuestID = 166;
local ReqClv = 12;
local ReqJlv = 0;
local NextQuest = 167;
local RewZeny = 212;
local RewCxp = 675;
local RewJxp = 0;
local RewWxp = 0;
local RewItem1 = 1700113;
local RewItem2 = 0;
local RewItemCount1 = 2;
local RewItemCount2 = 0;
local StepID = 0;

-- Modify steps below for gameplay

function QUEST_START(cid)
	Saga.AddStep(cid, QuestID, 16601);
	Saga.AddStep(cid, QuestID, 16602);
	Saga.InsertQuest(cid, QuestID, 1);
	return 0;
end

function QUEST_FINISH(cid)
	-- Gives all rewards
	Saga.GiveItem(cid, RewItem1, RewItemCount1);
	Saga.GiveZeny(cid, RewZeny);
	Saga.GiveExp(cid, RewCxp, RewJxp, RewWxp);
	return 0;
end

function QUEST_CANCEL(cid)
	return 0;
end

function QUEST_STEP_1(cid, StepID)
	-- Talk with Hena
	Saga.AddWaypoint(cid, QuestID, StepID, 1, 1152);
	
	-- Check for completion
	local ret = Saga.GetNPCIndex(cid);
	if ret == 1152 then
		Saga.GeneralDialog(cid, 2937);
		Saga.SubstepComplete(cid, QuestID, StepID, 1);
	end
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.ClearWaypoints(cid, QuestID);
	Saga.StepComplete(cid, QuestID, StepID);
	return 0;
end

function QUEST_STEP_2(cid, StepID)
	-- Harvest a Pumpkin (5)
	Saga.FindQuestItem(cid, QuestID, StepID, 31, 3978, 10000, 5, 1);
	Saga.FindQuestItem(cid, QuestID, StepID, 32, 3978, 10000, 5, 2);

	-- (De-)Activates the Action Objectd on request
	if Saga.IsSubStepCompleted(cid, QuestID, StepID, 1) == false then
		Saga.UserUpdateActionObjectType(cid, QuestID, StepID, 31, 0);
		Saga.UserUpdateActionObjectType(cid, QuestID, StepID, 32, 0);
	else
		Saga.UserUpdateActionObjectType(cid, QuestID, StepID, 31, 1);
		Saga.UserUpdateActionObjectType(cid, QuestID, StepID, 32, 1);
	end
	
	-- Check if all substeps are completed
	for i = 1, 3 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.ClearWaypoints(cid, QuestID);
	Saga.StepComplete(cid, QuestID, StepID);
	return 0;
end

function QUEST_STEP_3(cid, StepID)
	-- Bring the harvested pumpkin to Hena
	Saga.AddWaypoint(cid, QuestID, StepID, 1, 1152);
	
	-- Check for completion
	local ret = Saga.GetNPCIndex(cid);
	if ret == 1152 then
		Saga.GeneralDialog(cid, 2940);
		local ItemCountA = Saga.CheckUserInventory(cid, 3978);
		if ItemCountA > 4 then
			Saga.NpcTakeItem(cid, 3978, 5);
			Saga.SubstepComplete(cid, QuestID, StepID, 1);
		end
	end
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.ClearWaypoints(cid, QuestID);
	Saga.StepComplete(cid, QuestID, StepID);
	Saga.QuestComplete(cid, QuestID);
	return -1;
end

function QUEST_CHECK(cid)
	local CurStepID = Saga.GetStepIndex(cid, QuestID);
	local StepID = CurStepID;
	local ret = -1;

	if CurStepID == 16601 then
		ret = QUEST_STEP_1(cid, StepID);
	elseif CurStepID == 16602 then
		ret = QUEST_STEP_2(cid, StepID);
	elseif CurStepID == 16603 then
		ret = QUEST_STEP_3(cid, StepID);
	end
	
	if ret == 0 then
		QUEST_CHECK(cid);
	end
	
	return ret;
end
