local QuestID = 379;
local ReqClv = 25;
local ReqJlv = 0;
local NextQuest = 0;
local RewZeny = 1008;
local RewCxp = 4510;
local RewJxp = 1804;
local RewWxp = 0;
local RewItem1 = 1700114;
local RewItem2 = 0;
local RewItemCount1 = 7;
local RewItemCount2 = 0;
local StepID = 0;

-- Modify steps below for gameplay

function QUEST_START(cid)
	Saga.AddStep(cid, QuestID, 37901);
	Saga.AddStep(cid, QuestID, 37902);
	Saga.AddStep(cid, QuestID, 37903);
	Saga.AddStep(cid, QuestID, 37904);
	Saga.InsertQuest(cid, QuestID, 1);
	return 0;
end

function QUEST_FINISH(cid)
	-- Gives all rewards
	Saga.GiveItem(cid, RewItem1, RewItemCount1);
	Saga.GiveZeny(cid, RewZeny);
	Saga.GiveExp(cid, RewCxp, RewJxp, RewWxp);
	return 0;
end

function QUEST_CANCEL(cid)
	return 0;
end

function QUEST_STEP_1(cid, StepID)
	-- Visit Pretan
	Saga.AddWaypoint(cid, QuestID, StepID, 1, 1024);
	
	-- Check for completion
	local ret = Saga.GetNPCIndex(cid);
	if ret == 1024 then
		Saga.GeneralDialog(cid, 3766);
		Saga.SubstepComplete(cid, QuestID, StepID, 1);
	end
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.ClearWaypoints(cid, QuestID);
	Saga.StepComplete(cid, QuestID, StepID);
	return 0;
end

function QUEST_STEP_2(cid, StepID)
	-- Remove all posters in the time limit (10)
	Saga.FindQuestItem(cid, QuestID, StepID, 38, 4189, 10000, 10, 1);

	-- (De-)Activates the Action Objectd on request
	if Saga.IsSubStepCompleted(cid, QuestID, StepID, 1) == false then
		Saga.UserUpdateActionObjectType(cid, QuestID, StepID, 38, 0);
	else
		Saga.UserUpdateActionObjectType(cid, QuestID, StepID, 38, 1);
	end
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.StepComplete(cid, QuestID, StepID);
	return 0;
end

function QUEST_STEP_3(cid, StepID)
	-- Visit Pretan
	Saga.AddWaypoint(cid, QuestID, StepID, 1, 1024);
	
	-- Check for completion
	local ret = Saga.GetNPCIndex(cid);
	if ret == 1024 then
		Saga.GeneralDialog(cid, 3769);

		local ItemCountA = Saga.CheckUserInventory(cid, 4189);
		if ItemCountA > 9 then
			Saga.NpcTakeItem(cid, 4189, 10);
			Saga.SubstepComplete(cid, QuestID, StepID, 1);
		end
	end
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.ClearWaypoints(cid, QuestID);
	Saga.StepComplete(cid, QuestID, StepID);
	return 0;
end

function QUEST_STEP_4(cid, StepID)
	-- Visit Emiel
	Saga.AddWaypoint(cid, QuestID, StepID, 1, 1188);
	
	-- Check for completion
	local ret = Saga.GetNPCIndex(cid);
	if ret == 1188 then
		Saga.GeneralDialog(cid, 3772);

		local ItemCountA = Saga.CheckUserInventory(cid, 4189);
		if ItemCountA > 9 then
			Saga.NpcTakeItem(cid, 4189, 10);
			Saga.SubstepComplete(cid, QuestID, StepID, 1);
		end
	end
	
	-- Check if all substeps are completed
	for i = 1, 1 do
		if Saga.IsSubStepCompleted(cid, QuestID, StepID, i) == false then
			return -1;
		end
	end
	
	Saga.ClearWaypoints(cid, QuestID);
	Saga.StepComplete(cid, QuestID, StepID);
	Saga.QuestComplete(cid, QuestID);
	return -1;
end

function QUEST_CHECK(cid)
	local CurStepID = Saga.GetStepIndex(cid, QuestID);
	local StepID = CurStepID;
	local ret = -1;

	if CurStepID == 37901 then
		ret = QUEST_STEP_1(cid, StepID);
	elseif CurStepID == 37902 then
		ret = QUEST_STEP_2(cid, StepID);
	elseif CurStepID == 37903 then
		ret = QUEST_STEP_3(cid, StepID);
	elseif CurStepID == 37904 then
		ret = QUEST_STEP_4(cid, StepID);
	end
	
	if ret == 0 then
		QUEST_CHECK(cid);
	end
	
	return ret;
end
